  const [action, loading, error, lat, lng] = useLocation();

  return (
    <div>
      <button onClick={action} disabled={loading}>
        Get my position
      </button>

      {loading && <p>Loading position...</p>}
      {error && <p>{error}</p>}
      {!loading && !error && lat && lng && (
        <p>
          Your GPS position:{" "}
          <a
            target="_blank"
            rel="noreferrer"
            href={`https://www.openstreetmap.org/#map=16/${lat}/${lng}`}
          >
            {lat}, {lng}
          </a>
        </p>
      )}

      <p>You requested position {countClicks} times</p>
    </div>
  );
}
