import './styles.css'
import { useState } from "react";

function App() {
  const [step, setStep] = useState(1);
  const [count, setCount] = useState(0);
  const currentDate = new Date();

  function handileCountIncrease() {
    setCount((c) => c + 1)
  }
  function handileCountDecrease() {
    setCount((c) => c - 1)
  }

  return (
    <div className="App">
      <div className="stepLine">
        <button>-</button>
        <p>Step: {step}</p>
        <button>+</button>
      </div>
      <div className="countLine">
        <button>-</button>
        <p>count: {count}</p>
        <button>+</button>
      </div>

      <p className="statement">{count} days from today is {currentDate.getDate} </p>
    </div>
  );
}

export default App;
