import React, { useEffect } from 'react'

export default function Timer({ dispatch, seconds }) {
    useEffect(
        function () {
            const id = setInterval(function () {
                dispatch({ type: 'tick' })
            }, 1000);
            return () => clearInterval(id);
        }, [dispatch]
    )
    return (
        <div className='timer'>{seconds}</div>
    )
}
