import { useState } from 'react';
import './App.css';


const messages = [
  "Learn React âš›ï¸",
  "Apply for jobs ðŸ’¼",
  "Invest your new income ðŸ¤‘",
];

function App() {
  const [step, setStep] = useState(1)
  const [isOpen, setIsOpen] = useState(true)

  function handlePrevious() {
    step > 1 ? setStep((s) => (s - 1)) : setStep(step)
    {/* if (step > 1) {
      setStep(step - 1);
    }
    else {
      setStep(step)
    } */ }

  }
  function handleNext() {
    step < 3 ? setStep((s) => (s + 1)) : setStep(step)
    { /* if (step < 3) {

      setStep(step + 1);
    }
    else {
      setStep(step)
    } */ }

  }

  return (
    <>
      <button className='close' onClick={() => (isOpen ? setIsOpen(false) : setIsOpen(true))}>&times;</button>

      {isOpen && (<div className="steps">
        <div className='numbers'>
          <div className={step >= 1 ? 'active' : ""}>1</div>
          <div className={step >= 2 ? 'active' : ""}>2</div>
          <div className={step >= 3 ? 'active' : ""}>3</div>
        </div>

        <p className='message'>Step {step}: {messages[step - 1]}</p>

        <div className='buttons'>
          <button onClick={handlePrevious} style={{ backgroundColor: 'blue', color: 'white' }}>Previous</button>
          <button onClick={handleNext} style={{ backgroundColor: 'blue', color: 'white' }}>Next</button>
        </div>
      </div>)}
    </>
  );
}

export default App;
