import { useState } from 'react';
import './App.css';


const messages = [
  "Learn React ‚öõÔ∏è",
  "Apply for jobs üíº",
  "Invest your new income ü§ë",
];

function App() {
  const [step, setStep] = useState(1)

  function handlePrevious() {
    alert('Previous')
  }
  function handleNext() {
    alert('Next')
  }

  return (
    <div className="steps">
      <div className='numbers'>
        <div className={`${step >= 1 ? 'active' : ""}`}>1</div>
        <div className={`${step >= 2 ? 'active' : ""}`}>2</div>
        <div className={`${step >= 3 ? 'active' : ""}`}>3</div>
      </div>

      <p className='message'>Step {step}: {messages[step - 1]}</p>

      <div className='buttons'>
        <button onClick={handlePrevious} style={{ backgroundColor: 'blue', color: 'white' }}>Previous</button>
        <button onClick={handleNext} style={{ backgroundColor: 'blue', color: 'white' }}>Next</button>
      </div>

    </div>
  );
}

export default App;
