import { useState } from "react"
const options = [
  'Dissatisfied (0%)', 'It was okay (5%)', 'It was good (10%)', 'Absolutely amazing! (20%)'
]

function BillValue({ setBill }) {
  return (
    <div>
      <span>How much was the bill?</span> <input type="text" onChange={e => setBill(e.target.value)} />
    </div>
  )
}

function Rating({ text, options }) {
  return (
    <div>
      <span>{text}</span>
      <select>
        {options.map((i, index) => <option value={i.text} key={index}>{i}</option>)}
      </select>
    </div>
  )
}

function OutComes() {
  return (
    <div>
      <span>You pay</span>
    </div>
  )
}

function ResetBtn() {
  return (
    <>
      <button style={{ fontSize: '2rem', padding: '10px', marginTop: '30px' }} >Reset</button>
    </>
  )
}


export default function App() {
  const [bill, setBill] = useState('');
  const [mysatis, setMysatis] = useState(0);
  const [msatis, setMsatis] = useState(0);
  let output = 0;


  return (
    <div className="App">
      <BillValue setBill={setBill} />
      <Rating text={'How did you like the service? '} options={options} />
      <Rating text={'How did your friend like the service? '} options={options} />
      <OutComes bill={bill} mysatis={mysatis} msatis={msatis} />
      <ResetBtn />
    </div>
  )
}
