const options = [
  'Dissatisfied (0%)', 'It was okay (5%)', 'It was good (10%)', 'Absolutely amazing! (20%)'
]





function BillValue() {
  return (
    <div>
      <span>How much was the bill?</span> <input type="text" />
    </div>
  )
}

function Rating({ text, options }) {
  return (
    <div>
      <span>{text}</span>
      <select>
        {options.map((i, n) => <option value={i} key={n} />)}
      </select>
    </div>
  )
}




export default function App() {
  return (
    <div className="App">
      <BillValue />
      <Rating options={options} />
      <Rating options={options} />
    </div>
  )
}
